import React, { useState } from 'react'
import styles from './NovoPagamento.module.css'
import { render } from 'react-dom'

export default function NovoPagamento({onSubmit}) {
    const [novoItem, setNovoItem] = useState("")

    const setNovoPagamento = ({ target }) =>{
      setNovoItem(target.value)
    }

    const submit = (event) =>{
      event.preventDefault();
      onSubmit(novoItem)
      setNovoItem('')
      render(<div>Sucesso porra!</div>)
    }

  return (
    <div>
      <form onSubmit={submit}>
        <input
          onChange={setNovoPagamento}
          placeholder='Escreva a forma de pagamento'
          className={styles.input}
          value={novoItem}
          />
        <button type='submit' className={styles.botao}>Criar Pagamento</button>
      </form>
    </div>
  )
}
